section {
 background-repeat: no-repeat;
 background-attachment: fixed;
 background-size: cover;
 background-position: top;
 background-image:url(http://bit.ly/2gPLxZ4);
 width: 100%;
 height: 100vh;
 font-family: Arial, Helvetica;
 letter-spacing: 0.02em;
  font-weight: 400;
  h1 {
    position: relative;
    color: white;
    font-size: 30px;
    top: calc(40% - 175px);
  	left: calc(48% - 125px);
  }
}

.blurred-box{
  position: relative;
  width: 250px;
  height: 350px;
  top: calc(50% - 175px);
  left: calc(50% - 125px);
  background: inherit;
  border-radius: 2px;
  overflow: hidden;
  color: white;
}

.blurred-box:after{
 content: '';
 width: 300px;
 height: 300px;
 background: inherit; 
 position: absolute;
 left: -25px;
 right: 0;
 top: -25px;  
 bottom: 0;
 box-shadow: inset 0 0 0 200px rgba(255,255,255,0.05);
 filter: blur(10px);
}


/* Form which you dont need */
.user-login-box{
  position: relative;
  margin-top: 50px;
  text-align: center;
  z-index: 1;
}
.user-login-box > *{
  display: inline-block;
  width: 200px;
}


.user-name{
  margin-top: 15px;
  margin-bottom: 15px;
  color: white;
}

input.user-password{
  width: 120px;
  height: 18px;
  opacity: 0.4;
  border-radius: 2px;
  padding: 5px 15px;
  border: 0;
}

section {
  display: flex;
  justify-content: space-between;
  background-color: hsl(218, 28%, 13%);
  color: hsl(0, 0%, 100%);
	margin: 0.3rem 1.1rem;
  padding-left: 1rem;
  > logo {
    font-size: 3rem;
    font-family: 'Raleway', sans-serif;
    weight: 400;
    }
  > nav {
    padding-top: 0.5rem;
    font-size: 2rem;
  }
}
